  $: showBuildStep = false;
  $: showDeployStep = false;
  $: showManageStep = false;

  onMount(async () => {
    const howWorksObserver = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          showBuildStep = true;
          // wait for 300ms before showing the next step
          setTimeout(() => {
            showDeployStep = true;
          }, 600);

          // wait for 600ms before showing the next step
          setTimeout(() => {
            showManageStep = true;
          }, 1200);
        }
      });
    });

    howWorksObserver.observe(document.querySelector(".how-it-works"));
  });
